CREATE OR REPLACE PROCEDURE liberar_servico_nutricao_lista ( nr_seq_nut_serv_lista_p text, nm_usuario_p text, ie_acao_p text) AS $body$
DECLARE


nr_seq_nut_serv_lista_w	varchar(4000);
nr_seq_nut_serv_w		bigint;
qt_servico_liberado_w	bigint;


BEGIN

nr_seq_nut_serv_lista_w	:= nr_seq_nut_serv_lista_p;

while (nr_seq_nut_serv_lista_w IS NOT NULL AND nr_seq_nut_serv_lista_w::text <> '') loop
	begin
	nr_seq_nut_serv_w		:= substr(nr_seq_nut_serv_lista_w, 1, position(',' in nr_seq_nut_serv_lista_w) - 1);
	nr_seq_nut_serv_lista_w	:= substr(nr_seq_nut_serv_lista_w, position(',' in nr_seq_nut_serv_lista_w) + 1, length(nr_seq_nut_serv_lista_w));
	
	select	count(*)
	into STRICT	qt_servico_liberado_w
	from 	nut_atend_serv_dia
	where	nr_sequencia = nr_seq_nut_serv_w
	and (coalesce(dt_liberacao::text, '') = '' or coalesce(dt_lib_servico_acomp::text, '') = '');
	
	if (coalesce(qt_servico_liberado_w,0) > 0) then

		CALL liberar_servico_nutricao(nr_seq_nut_serv_w, nm_usuario_p, ie_acao_p);
	end if;	
	
	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
