CREATE OR REPLACE PROCEDURE liberar_sistema_corpo_hum ( nr_seq_sis_corp_hum_p bigint, nm_usuario_p text) AS $body$
DECLARE

dt_liberacao_w timestamp;

BEGIN


if (nr_seq_sis_corp_hum_p IS NOT NULL AND nr_seq_sis_corp_hum_p::text <> '') then
	select dt_liberacao into STRICT dt_liberacao_w from sistema_corpo_humano where nr_sequencia = nr_seq_sis_corp_hum_p;
	if (coalesce(dt_liberacao_w::text, '') = '') then
		update	sistema_corpo_humano
		set		dt_liberacao = clock_timestamp(),
				dt_atualizacao = clock_timestamp(),
				nm_usuario =  nm_usuario_p
		where	nr_sequencia = nr_seq_sis_corp_hum_p;
	else
		update	sistema_corpo_humano
		set		dt_liberacao  = NULL,
				dt_atualizacao = clock_timestamp(),
				nm_usuario =  nm_usuario_p
		where	nr_sequencia = nr_seq_sis_corp_hum_p;
	end if;
	commit;

	
end if;


end;
$body$
