SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_sorologia_hemot ( nr_seq_doacao_p bigint, ie_opcao_p text, ie_consistir_p text, nm_usuario_seg_lib_p text, nm_usuario_p text, ds_pergunta_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE


/*	ie_opcao_p
	L - liberar
	D - desfazer	*/
ie_hemocomp_utilizados_w	varchar(1);
ie_opcao_w			varchar(1);
nr_atendimento_w		atendimento_paciente.nr_atendimento%type;


BEGIN

if (ie_opcao_p = 'L') then
	begin
	update	san_doacao
	set	ie_status		= 3,
		dt_liberacao	= clock_timestamp(),
		nm_usuario_lib	= nm_usuario_p,
		ie_status_ant	= ie_status,
		nm_usuario_seg_lib	= nm_usuario_seg_lib_p
	where	nr_sequencia	= nr_seq_doacao_p;
	
	CALL San_Gerar_Codigo_ISBT.San_gravar_cod_exame_isbt(nr_seq_doacao_p);
	
	--SAN Evento - LIBERAR SOROLOGIA
	CALL executa_evento_hemoterapia( nm_usuario_p , nr_seq_doacao_p , 4);
	
	end;

elsif (ie_opcao_p = 'D') then
	begin
	if (ie_consistir_p = 'S') then
		begin
		
		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_hemocomp_utilizados_w
		from	san_producao
		where	nr_seq_doacao = nr_seq_doacao_p
		and ((nr_seq_reserva IS NOT NULL AND nr_seq_reserva::text <> '')
			or (nr_seq_transfusao IS NOT NULL AND nr_seq_transfusao::text <> ''));

		if (ie_hemocomp_utilizados_w = 'S') then
			ds_erro_p		:= obter_texto_tasy(82806, wheb_usuario_pck.get_nr_seq_idioma);
		else
			ds_pergunta_p	:= obter_texto_tasy(82807, wheb_usuario_pck.get_nr_seq_idioma);
		end if;
		end;
	else
		begin
		update	san_doacao
		set	ie_status		= ie_status_ant,
			dt_liberacao	 = NULL,
			nm_usuario_lib	 = NULL,
			nm_usuario_seg_lib	 = NULL,
			ie_status_ant	= ie_status
		where	nr_sequencia	= nr_seq_doacao_p;
		end;
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
