CREATE OR REPLACE PROCEDURE liberar_sug_sugestao_hist ( nr_sequencia_p bigint, ie_liberacao_p text) AS $body$
BEGIN

if (ie_liberacao_p = 'L') then

	update	sug_sugestao_hist
	set	dt_liberacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;

elsif (ie_liberacao_p = 'D') then

	update	sug_sugestao_hist
	set	dt_liberacao	 = NULL
	where	nr_sequencia	= nr_sequencia_p;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
