CREATE OR REPLACE PROCEDURE liberar_sv_icca (nm_tabela_p text, nm_chave_p text, qt_chave_p bigint, nm_campo_p text, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE


qt_chave_w bigint;
sequencia_w varchar(900):='';
ds_sql varchar(900):='';

BEGIN
    qt_chave_w := qt_chave_p;
    if (qt_chave_p IS NOT NULL AND qt_chave_p::text <> '') and (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') and (nm_chave_p IS NOT NULL AND nm_chave_p::text <> '')
       and (nm_campo_p IS NOT NULL AND nm_campo_p::text <> '') then             
        qt_chave_w := Duplicar_Registro(nm_tabela_p, nm_usuario_p, qt_chave_w);
        select cast( qt_chave_w as varchar(99))into sequencia_w;
        ds_sql := 'update '||nm_tabela_p ||' set '|| nm_campo_p||' = null where '||nm_chave_p||' = '||sequencia_w;
        EXECUTE(ds_sql);
        CALL inativar_informacao(nm_tabela_p,nm_chave_p,qt_chave_p,ds_justificativa_p,nm_usuario_p);
        CALL liberar_informacao(nm_tabela_p,nm_chave_p,qt_chave_w,nm_usuario_p);
    else
        CALL inativar_informacao(nm_tabela_p,nm_chave_p,qt_chave_p,ds_justificativa_p,nm_usuario_p);
    end if;
end;
