CREATE OR REPLACE PROCEDURE liberar_tarefa_tasy ( nr_sequencia_p bigint, ie_tipo_acao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* IE_TIPO_ACAO_P
	1 - Liberar
	2 - Desfazer liberação
*/
nm_usuario_liberacao_w		varchar(15);
dt_liberacao_w			timestamp;


BEGIN
nm_usuario_liberacao_w	:= '';
dt_liberacao_w		:= null;

if (ie_tipo_acao_p	= 1) then
	nm_usuario_liberacao_w	:= nm_usuario_p;
	dt_liberacao_w		:= clock_timestamp();
end if;

update	tarefa_tasy
set	dt_liberacao		= dt_liberacao_w,
	nm_usuario_liberacao	= nm_usuario_liberacao_w,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
