CREATE OR REPLACE PROCEDURE liberar_titulo_js ( nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_pendentes_w	integer;
qt_registros_liberados_w	integer;
qt_min_usuario_lib_w 		bigint;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	update  	conta_pagar_lib a
	set     	dt_liberacao   = CASE WHEN dt_liberacao = NULL THEN  clock_timestamp()  ELSE null END ,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where   	((a.ie_nivel = (
			SELECT   	min(x.ie_nivel)
			from     	conta_pagar_lib x
			where    	x.nm_usuario_lib  = nm_usuario_p
			and      	x.nr_titulo       = nr_titulo_p)) or (coalesce(a.ie_nivel::text, '') = ''))
	and     	nm_usuario_lib = nm_usuario_p
	and     	nr_titulo      = nr_titulo_p;	
	
	select  	count(*)
	into STRICT		qt_registros_pendentes_w
	from    	conta_pagar_lib a
	where   	coalesce(a.dt_liberacao::text, '') = ''
	and     	a.nr_titulo = nr_titulo_p;	

	select  	count(*)
	into STRICT		qt_registros_liberados_w
	from    	conta_pagar_lib a
	where   	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and     	a.nr_titulo = nr_titulo_p;	
	
	select 	max(coalesce(b.qt_min_usuario_lib,0))
	into STRICT 	qt_min_usuario_lib_w
	from 	conta_pagar_lib a,
			regra_lib_cp b
	where	a.nr_seq_regra_lib = b.nr_sequencia
	and 	a.nr_titulo = nr_titulo_p;					

	if	((qt_registros_pendentes_w = 0) or (qt_min_usuario_lib_w <> 0 AND qt_registros_liberados_w >= qt_min_usuario_lib_w)) then
		CALL liberar_desfazer_lib_titulo(nm_usuario_p, nr_titulo_p);
	end if;
	
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
