SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_titulo_orgao_cobr ( nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_w
from	titulo_receber_orgao_cobr
where	nr_titulo = nr_titulo_p
and	coalesce(dt_liberacao_exclusao::text, '') = '';

if (qt_w > 0) then

	update	titulo_receber_orgao_cobr
	set	dt_liberacao_exclusao = clock_timestamp()
	where	nr_titulo = nr_titulo_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
