CREATE OR REPLACE PROCEDURE liberar_usuarios_perfil_trans ( nr_seq_trans_financ_p bigint, cd_perfil_p bigint, nm_usuario_p text, qt_usuario_p INOUT bigint) AS $body$
DECLARE

qt_usuario_w	bigint	:= 0;
nm_usuario_w	varchar(15);

c01 CURSOR FOR
SELECT	a.nm_usuario
from	usuario b,
	usuario_perfil a
where	a.cd_perfil	= cd_perfil_p
and	a.nm_usuario	= b.nm_usuario
and	b.ie_situacao	= 'A'
and	not exists (select	1
	from	trans_financ_usuario x
	where	x.nr_seq_transacao	= nr_seq_trans_financ_p
	and	x.nm_usuario_param	= a.nm_usuario);


BEGIN

open c01;
loop
fetch c01 into
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	insert	into trans_financ_usuario(dt_atualizacao,
		nm_usuario,
		nm_usuario_param,
		nr_seq_transacao,
		nr_sequencia)
	values (clock_timestamp(),
		nm_usuario_p,
		nm_usuario_w,
		nr_seq_trans_financ_p,
		nextval('trans_financ_usuario_seq'));

	qt_usuario_w	:= qt_usuario_w + 1;

end loop;
close c01;

qt_usuario_p	:= qt_usuario_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
