CREATE OR REPLACE PROCEDURE libera_reacao_medico ( nm_usuario_p text, nr_seq_doacao_p bigint) AS $body$
DECLARE



cd_evolucao_w evolucao_paciente.cd_evolucao%TYPE;
nr_atendimento_w atendimento_paciente.nr_atendimento%TYPE;
nr_sequnecia_w san_trans_reacao.nr_sequencia%TYPE;

BEGIN

update 	san_trans_reacao
set		dt_lib_medico = clock_timestamp(),
		nm_usuario_medico = nm_usuario_p
where 	nr_sequencia = nr_seq_doacao_p;

commit;

if (coalesce(pkg_i18n.get_user_locale, 'pt_BR') = 'ja_JP') then
	begin

	SELECT
		tr.nr_atendimento,
		tr.nr_sequencia
	INTO STRICT
		nr_atendimento_w,
		nr_sequnecia_w
	FROM
		san_producao_v     a,
		san_transfusao     tr,
		san_trans_reacao   str
	WHERE
		tr.nr_sequencia = a.nr_seq_transfusao
		AND a.nr_sequencia = str.nr_seq_producao
		AND (str.dt_lib_medico IS NOT NULL AND str.dt_lib_medico::text <> '')
		AND str.nr_sequencia = nr_seq_doacao_p;
	
	cd_evolucao_w := clinical_notes_pck.gerar_soap(nr_atendimento_w, nr_sequnecia_w, 'CPOE_ITEMS', 5, 'P', 2, cd_evolucao_w, 're');
	EXCEPTION
		WHEN no_data_found THEN
			NULL;
		WHEN too_many_rows THEN
			NUll;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
