SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lib_desf_acordo_comercial (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_liberacao_w		timestamp;


BEGIN
select	dt_liberacao
into STRICT	dt_liberacao_w
from	com_cliente_acordo
where	nr_sequencia = nr_sequencia_p;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then

	update	com_cliente_acordo
	set	dt_liberacao  = NULL, nm_usuario_liberacao = ''
	where	nr_sequencia = nr_sequencia_p;
else

	update	com_cliente_acordo
	set	dt_liberacao = clock_timestamp(), nm_usuario_liberacao = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
