CREATE OR REPLACE PROCEDURE lib_hor_incorreto_agenda_pac ( dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


							
nr_seq_agenda_w		agenda_paciente.nr_sequencia%type;								


C01 CURSOR FOR
	SELECT	 a.nr_sequencia
	into STRICT	 nr_seq_agenda_w
	from	 agenda_paciente a,
		 agenda b
	where	 a.cd_agenda = b.cd_agenda
	and	 a.dt_agenda between dt_inicial_p and dt_final_p
	AND	 coalesce(a.ie_status_agenda,'L')	= 'N'
	and	 a.nm_usuario_acesso		= nm_usuario_p
	and	 coalesce(a.cd_pessoa_fisica::text, '') = ''
	and	 coalesce(a.nm_paciente::text, '') = ''
	and	 b.cd_estabelecimento 		= cd_estabelecimento_p
	order by nr_sequencia;
	

BEGIN
begin

	open C01;
	loop
	fetch C01 into	
		nr_seq_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		CALL Liberar_Horario_Agenda_Exame(nr_seq_agenda_w,nm_usuario_p);
		
		end;
	end loop;
	close C01;

exception
when others then
	null;
end;

end;
$body$
