CREATE OR REPLACE PROCEDURE lib_inativ_aval_agenda_cir ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (ie_opcao_p = 'L') then 
	/* 
	update med_avaliacao_paciente 
	set dt_liberacao = sysdate, 
	  nm_usuario = nm_usuario_p 
	where nr_sequencia = nr_sequencia_p; 
	*/
 
	CALL Liberar_avaliacao(nr_sequencia_p,nm_usuario_p);
 
elsif (ie_opcao_p = 'I') then 
	UPDATE	med_avaliacao_paciente 
	SET	nm_usuario_inativacao = nm_usuario_p, 
		IE_SITUACAO	= 'I', 
		dt_inativacao = clock_timestamp() 
	WHERE	nr_sequencia = nr_sequencia_p;
 
COMMIT;
 
end if;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
