SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lib_inativ_observacao_cir ( ie_opcao_p text, nr_sequencia_p bigint, nm_usuario_p text, ds_justificativa_p text) AS $body$
BEGIN

begin

if (ie_opcao_p = 'L') and (coalesce(nr_sequencia_p,0) > 0) then

		update	cirurgia_observacao
		set 	dt_liberacao 	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where 	nr_sequencia 	= nr_sequencia_p;
		commit;

elsif (ie_opcao_p = 'I') and (coalesce(nr_sequencia_p,0) > 0) then

		update	cirurgia_observacao
		set 	dt_inativacao = clock_timestamp(),
			nm_usuario_inativacao = nm_usuario_p,
			ds_justificativa = ds_justificativa_p,
			ie_situacao = 'I'
		where 	nr_sequencia = nr_sequencia_p;
		commit;
end if;

exception
when others then
	null;
end;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
