CREATE OR REPLACE PROCEDURE lib_inat_reg_funcao_macro_ini ( nm_usuario_p text, nr_sequencia_p bigint, ie_opcao_p text, ds_justificativa_inat_p text) AS $body$
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (ds_justificativa_inat_p IS NOT NULL AND ds_justificativa_inat_p::text <> '') then

	if (ie_opcao_p = 'L') then

		update	funcao_macro_inicial
		set 	dt_liberacao 	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where 	nr_sequencia	= nr_sequencia_p;

	elsif (ie_opcao_p = 'I') then

		update	funcao_macro_inicial
		set 	dt_inativacao 	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp(),
			ds_justificativa 	= substr(ds_justificativa_inat_p,1,255)
		where 	nr_sequencia	= nr_sequencia_p;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
