SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lib_tecni_descri_anestesica ( nr_sequencia_p bigint, nm_tabela_p text, nm_usuario_p text, ie_html_p text default 'N') AS $body$
DECLARE


ie_evolucao_clinica_w	varchar(3);
nr_cirurgia_w	varchar(10);
nr_seq_pepo_w	varchar(10);
ie_gera_evolucao_w	varchar(3);


BEGIN
if (upper(nm_tabela_p) = 'CIRURGIA_TEC_ANESTESICA') then
	begin
	update 	cirurgia_tec_anestesica
	set	dt_liberacao	=	clock_timestamp()
	where	nr_sequencia	=	nr_sequencia_p
	and	nm_usuario	=	nm_usuario_p
	and	coalesce(dt_liberacao::text, '') = '';
	end;
else
	begin
	update 	anestesia_descricao
	set	dt_liberacao	=	clock_timestamp()
	where	nr_sequencia	=	nr_sequencia_p
	and	nm_usuario	=	nm_usuario_p
	and	coalesce(dt_liberacao::text, '') = '';

	commit;

	ie_gera_evolucao_w := obter_param_usuario(872, 525, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_gera_evolucao_w);

	if (coalesce(ie_gera_evolucao_w,'N') = 'S') then

		select	coalesce(max(nr_cirurgia),0),
				coalesce(max(nr_seq_pepo),0)
		into STRICT	nr_cirurgia_w,
				nr_seq_pepo_w
		from 	anestesia_descricao
		where	nr_sequencia = nr_sequencia_p;

		if ((coalesce(nr_cirurgia_w,0) > 0)
			or (coalesce(nr_seq_pepo_w,0) > 0)) then
			CALL gerar_evolucao_anestesia(nr_cirurgia_w, nr_seq_pepo_w, nr_sequencia_p, nm_usuario_p, coalesce(ie_html_p,'N'));
		end if;
	end if;

	end;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
