CREATE OR REPLACE PROCEDURE lib_tecni_descri_anest_prot ( nr_sequencia_p bigint, nm_tabela_p text, nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_cirurgia_w	bigint;
nr_seq_pepo_w	bigint;


BEGIN
select	nr_cirurgia,
	nr_seq_pepo
into STRICT	nr_cirurgia_w,
	nr_seq_pepo_w
from	cirurgia_tec_anestesica
where	nr_sequencia = nr_sequencia_p;

if (upper(nm_tabela_p) = 'CIRURGIA_TEC_ANESTESICA') then
	begin
	if (coalesce(nr_seq_protocolo_p,0) > 0) then
		update 	cirurgia_tec_anestesica
		set	dt_liberacao	=	clock_timestamp()
		where	nm_usuario	=	nm_usuario_p
		and	coalesce(dt_liberacao::text, '') = ''
		AND	nr_seq_protocolo =	nr_seq_protocolo_p
		and	((nr_cirurgia	= nr_cirurgia_w AND nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') or (nr_seq_pepo = nr_seq_pepo_w AND nr_seq_pepo_w IS NOT NULL AND nr_seq_pepo_w::text <> ''));
	else
		update 	cirurgia_tec_anestesica
		set	dt_liberacao	=	clock_timestamp()
		where	nr_sequencia	=	nr_sequencia_p
		and	nm_usuario	=	nm_usuario_p
		and	coalesce(dt_liberacao::text, '') = '';
	end if;
	end;
else
	begin
	if (coalesce(nr_seq_protocolo_p,0) > 0) then
		update 	ANESTESIA_DESCRICAO
		set	dt_liberacao	=	clock_timestamp()
		where	nm_usuario	=	nm_usuario_p
		and	coalesce(dt_liberacao::text, '') = ''
		AND	nr_seq_protocolo=	nr_seq_protocolo_p
		and	((nr_cirurgia	= nr_cirurgia_w AND nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') or (nr_seq_pepo = nr_seq_pepo_w AND nr_seq_pepo_w IS NOT NULL AND nr_seq_pepo_w::text <> ''));
	else
		update 	anestesia_descricao
		set	dt_liberacao	=	clock_timestamp()
		where	nr_sequencia	=	nr_sequencia_p
		and	nm_usuario	=	nm_usuario_p
		and	coalesce(dt_liberacao::text, '') = '';
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
