CREATE OR REPLACE PROCEDURE lic_adiciona_proximas_empresas ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint, nr_seq_fornec_p bigint, nr_seq_lance_p bigint, vl_item_p bigint, nm_usuario_p text) AS $body$
DECLARE



vl_melhor_inicial_w				double precision;
nr_seq_lic_item_fornec_w		bigint;


BEGIN

select	nr_sequencia
into STRICT	nr_seq_lic_item_fornec_w
from	reg_lic_item_fornec
where	nr_seq_fornec	= nr_seq_fornec_p
and	nr_seq_lic_item	= nr_seq_lic_item_p;

select	lic_obter_valor_vencedor_item(nr_seq_licitacao_p, nr_seq_lic_item_p)
into STRICT	vl_melhor_inicial_w
;

insert into reg_lic_fornec_lance(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_licitacao,
	nr_seq_fornec,
	nr_seq_lance,
	nr_seq_lic_item,
	nr_seq_lic_item_fornec,
	vl_item,
	vl_inicial,
	ie_qualificado,
	vl_melhor_inicial,
	ie_reenserida)
values (	nextval('reg_lic_fornec_lance_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_licitacao_p,
	nr_seq_fornec_p,
	nr_seq_lance_p,
	nr_seq_lic_item_p,
	nr_seq_lic_item_fornec_w,
	null,
	vl_item_p,
	'S',
	vl_melhor_inicial_w,
	'S');

commit;

end;
