SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_adic_reg_lic_compra_item ( nr_seq_reg_lic_compra_p bigint, nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint, nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_reg_compra_w			bigint;
nr_seq_fornec_w				bigint;
nr_seq_lic_item_fornec_w			bigint;
cd_material_w				integer;
qt_material_w				double precision;
cd_unidade_medida_compra_w		varchar(30);
vl_item_w					double precision;


BEGIN
select	cd_material,
	cd_unidade_medida_compra
into STRICT	cd_material_w,
	cd_unidade_medida_compra_w
from	solic_compra_item
where	nr_solic_compra		= nr_solic_compra_p
and	nr_item_solic_compra	= nr_item_solic_compra_p;

select	coalesce(max(qt_item),0)
into STRICT	qt_material_w
from	reg_lic_item_solic
where	nr_seq_licitacao		= nr_seq_licitacao_p
and	nr_seq_lic_item		= nr_seq_lic_item_p
and	nr_solic_compra		= nr_solic_compra_p
and	nr_item_solic_compra	= nr_item_solic_compra_p;

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_seq_reg_compra_w
from	reg_compra a,
	reg_compra_item b
where	a.nr_sequencia		= b.nr_seq_reg_compra
and	a.nr_seq_licitacao 		= nr_seq_licitacao_p
and	b.nr_seq_lic_item		= nr_seq_lic_item_p
and	coalesce(b.dt_cancelamento::text, '') = '';

select	coalesce(max(vl_item),0)
into STRICT	vl_item_w
from	reg_compra_item
where	nr_seq_reg_compra	= nr_seq_reg_compra_w
and	nr_seq_lic_item		= nr_seq_lic_item_p
and	coalesce(dt_cancelamento::text, '') = '';

if (nr_seq_reg_compra_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266168,'NR_SEQ_LICITACAO=' || nr_seq_licitacao_p);
	--Não foi gerado nenhum registro de preço para a licitação número ' ||nr_seq_licitacao_p
end if;

select	max(nr_seq_fornec),
	max(nr_seq_lic_item_fornec)
into STRICT	nr_seq_fornec_w,
	nr_seq_lic_item_fornec_w
from	reg_compra_item
where	nr_seq_licitacao = nr_seq_licitacao_p
and	nr_seq_lic_item = nr_seq_lic_item_p
and	coalesce(dt_cancelamento::text, '') = '';

insert into reg_lic_compra_item(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	nr_seq_reg_lic_compra,
	nr_seq_licitacao,
	nr_seq_lic_item,
	nr_solic_compra,
	nr_item_solic_compra,
	nr_seq_fornec,
	nr_seq_lic_item_fornec,
	cd_material,
	qt_material,
	cd_unidade_medida_compra,
	nr_seq_reg_compra,
	vl_item)
values (	nextval('reg_lic_compra_item_seq'),
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_reg_lic_compra_p,
	nr_seq_licitacao_p,
	nr_seq_lic_item_p,
	nr_solic_compra_p,
	nr_item_solic_compra_p,
	nr_seq_fornec_w,
	nr_seq_lic_item_fornec_w,
	cd_material_w,
	qt_material_w,
	cd_unidade_medida_compra_w,
	nr_seq_reg_compra_w,
	vl_item_w);

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lic_adic_reg_lic_compra_item ( nr_seq_reg_lic_compra_p bigint, nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint, nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nm_usuario_p text) FROM PUBLIC;

