CREATE OR REPLACE PROCEDURE lic_alterar_fornec_reg_preco ( nr_sequencia_p bigint, nr_seq_fornec_p bigint, vl_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_reg_compra_w			bigint;					
cd_material_w				integer;
ds_material_w				varchar(255);
ds_historico_w				varchar(4000);
nr_seq_fornec_w				bigint;
ds_velho_fornec_w				varchar(100);
ds_novo_fornec_w				varchar(100);
vl_item_w					double precision;

BEGIN
 
select	nr_seq_reg_compra, 
	nr_seq_fornec, 
	cd_material, 
	substr(obter_desc_material(cd_material),1,255), 
	vl_item 
into STRICT	nr_seq_reg_compra_w, 
	nr_seq_fornec_w, 
	cd_material_w, 
	ds_material_w, 
	vl_item_w 
from	reg_compra_item 
where	nr_sequencia	= nr_sequencia_p;
 
update	reg_compra_item 
set	nr_seq_fornec	= nr_seq_fornec_p, 
	vl_item		= vl_item_p 
where	nr_sequencia	= nr_sequencia_p;
 
update	reg_compra_item 
set	nr_seq_fornec_original	= nr_seq_fornec_p 
where	nr_sequencia		= nr_sequencia_p 
and	coalesce(nr_seq_fornec_original::text, '') = '';
 
select	obter_fornec_reg_lic_fornec(nr_seq_fornec_p), 
	obter_fornec_reg_lic_fornec(nr_seq_fornec_w) 
into STRICT	ds_novo_fornec_w, 
	ds_velho_fornec_w
;
 
ds_historico_w := 	substr(wheb_mensagem_pck.get_texto(802051,'CD_MATERIAL_W='|| cd_material_w ||';DS_MATERIAL_W='|| ds_material_w ||';NR_SEQ_REG_COMPRA_W='|| nr_seq_reg_compra_w ||';DS_VELHO_FORNEC_W='|| ds_velho_fornec_w 
				||';VL_ITEM_W='|| campo_mascara(vl_item_w,4) ||';DS_NOVO_FORNEC_W='|| ds_novo_fornec_w ||';VL_ITEM_P='|| campo_mascara(vl_item_p,4)),1,4000);
 
		 
			 
CALL lic_gerar_historico_reg_preco(nr_seq_reg_compra_w, ds_historico_w, 'S', nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
