SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_altera_estagio (nr_seq_licitacao_p bigint, nr_seq_novo_estagio_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_estagio_old_w	bigint;
ds_estagio_novo_w	varchar(255);
ds_estagio_old_w	varchar(255);


BEGIN
	select 	nr_seq_estagio
	into STRICT	nr_seq_estagio_old_w
	from	REG_LICITACAO
	where	nr_sequencia = nr_seq_licitacao_p;

	update	REG_LICITACAO
	set	NR_SEQ_ESTAGIO = nr_seq_novo_estagio_p
	where	nr_sequencia = nr_seq_licitacao_p;

	select 	substr(ds_estagio,1,255)
	into STRICT	ds_estagio_novo_w
	from  	lic_estagio
	where	nr_Sequencia = nr_seq_novo_estagio_p;

	select 	substr(ds_estagio,1,255)
	into STRICT	ds_estagio_old_w
	from  	lic_estagio
	where	nr_Sequencia = nr_seq_estagio_old_w;




	insert into REG_LIC_HISTORICO(nr_sequencia,
				       dt_atualizacao,
				       nm_usuario,
				       ie_tipo_historico,
				       ds_observacao,
				       nr_seq_licitacao)
	values			      (nextval('reg_lic_historico_seq'),
				       clock_timestamp(),
				       nm_usuario_p,
				       'ALE',
				       WHEB_MENSAGEM_PCK.get_texto(310362, 'DS_ESTAGIO_OLD_W=' || ds_estagio_old_w) ||  ds_estagio_novo_w, --'Estágio anterior: ' || ds_estagio_old_w || '. Novo estágio: ' || ds_estagio_novo_w,
				       nr_seq_licitacao_p);





commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
