SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_aprovar_edital ( nr_sequencia_p bigint, dt_aprovacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_licitacao_w			bigint;
nr_lote_compra_w			varchar(80);
nr_seq_lic_item_w			integer;
qt_item_w				double precision;
vl_maximo_edital_w			double precision;

c01 CURSOR FOR
SELECT	distinct nr_lote_compra,
	lic_obter_dados_lote(nr_seq_licitacao, nr_lote_compra,2) qt_item,
         lic_obter_dados_lote(nr_seq_licitacao, nr_lote_compra,1) vl_maximo_edital
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_w
and	(nr_lote_compra IS NOT NULL AND nr_lote_compra::text <> '');


BEGIN

select	nr_seq_licitacao
into STRICT	nr_seq_licitacao_w
from	reg_lic_edital
where	nr_sequencia	= nr_sequencia_p;

update	reg_lic_edital
set	dt_aprovacao_edital	= dt_aprovacao_p,
	nm_usuario_aprov		= nm_usuario_p
where	nr_seq_licitacao		= nr_seq_licitacao_w
and	nr_sequencia		= nr_sequencia_p;

insert into reg_lic_historico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	ie_tipo_historico,
	ds_observacao,
	nr_seq_licitacao)
values (	nextval('reg_lic_historico_seq'),
	clock_timestamp(),
	nm_usuario_p,
	'AE',
	wheb_mensagem_pck.get_Texto(802130, 'NR_SEQUENCIA_P='|| NR_SEQUENCIA_P),
	nr_seq_licitacao_w);


open C01;
loop
fetch C01 into
	nr_lote_compra_w,
	qt_item_w,
	vl_maximo_edital_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(nr_seq_lic_item),0) +1
	into STRICT	nr_seq_lic_item_w
	from	reg_lic_item
	where	nr_seq_licitacao = nr_seq_licitacao_w;

	insert into reg_lic_item(
		dt_atualizacao,
		nm_usuario,
		nr_seq_licitacao,
		nr_seq_lic_item,
		cd_material,
		cd_unid_medida,
		ds_adicional,
		qt_item,
		vl_maximo_edital,
		pr_inflacao,
		nr_lote_compra,
		nr_seq_lote,
		ie_lote)
	values (	clock_timestamp(),
		nm_usuario_p,
		nr_seq_licitacao_w,
		nr_seq_lic_item_w,
		null,
		null,
		null,
		qt_item_w,
		vl_maximo_edital_w,
		null,
		nr_lote_compra_w,
		null,
		'S');

	update	reg_lic_item
	set	nr_seq_lote 		= nr_seq_lic_item_w,
		ie_lote			= 'N'
	where	nr_seq_licitacao	= nr_seq_licitacao_w
	and	coalesce(ie_lote,'N')	= 'N'
	and	nr_lote_compra		= nr_lote_compra_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
