SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_confirmar_edital ( nr_seq_licitacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;
ie_consiste_vl_proj_rec_w	varchar(1);
nr_seq_proj_rec_w		bigint;
cd_material_w			bigint;
vl_maximo_edital_w		double precision;
cd_estabelecimento_w		bigint;

c01 CURSOR FOR
SELECT	cd_material,
	coalesce(vl_maximo_edital,0),
	nr_seq_proj_rec
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p
and	(cd_material IS NOT NULL AND cd_material::text <> '');


BEGIN

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	reg_licitacao
where	nr_sequencia = nr_seq_licitacao_p;

select	substr(coalesce(obter_valor_param_usuario(952, 15, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w),'N'),1,1)
into STRICT	ie_consiste_vl_proj_rec_w
;

select	count(*)
into STRICT	qt_existe_w
from	reg_lic_edital
where	nr_seq_licitacao = nr_seq_licitacao_p
and	(dt_homologacao_edital IS NOT NULL AND dt_homologacao_edital::text <> '');

if (qt_existe_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266172);
	--'Não é possível confirmar o edital, pois o mesmo já foi homologado.');
end if;

select	count(*)
into STRICT	qt_existe_w
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p
and	coalesce(vl_maximo_edital,0) = 0;

if (qt_existe_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266174);
	--'Deve ser informado preço máximo do edital para todos os itens desta licitação. #@#@');
end if;

if (ie_consiste_vl_proj_rec_w = 'S') then

	open C01;
	loop
	fetch C01 into
		cd_material_w,
		vl_maximo_edital_w,
		nr_seq_proj_rec_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (nr_seq_proj_rec_w > 0) and (obter_se_item_proj_recurso(nr_seq_proj_rec_w, cd_material_w) = 'S') and (vl_maximo_edital_w > coalesce(obter_dados_item_proj_recurso(nr_seq_proj_rec_w, cd_material_w, 'V'),0)) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266175,'CD_MATERIAL=' || cd_material_w ||
									';DS_DADOS=' || campo_mascara_virgula(coalesce(obter_dados_item_proj_recurso(nr_seq_proj_rec_w, cd_material_w, 'V'),0)));
			--'O valor do material ' || cd_material_w || ' ultrapassou o valor do projeto recurso (' ||
				--campo_mascara_virgula(nvl(obter_dados_item_proj_recurso(nr_seq_proj_rec_w, cd_material_w, 'V'),0)) || ').');
		end if;

		end;
	end loop;
	close C01;

end if;

update	reg_licitacao
set	dt_confirmacao_edital = clock_timestamp(),
	nm_usuario_confirma = nm_usuario_p
where	nr_sequencia = nr_seq_licitacao_p;

insert into reg_lic_historico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	ie_tipo_historico,
	ds_observacao,
	nr_seq_licitacao)
values (	nextval('reg_lic_historico_seq'),
	clock_timestamp(),
	nm_usuario_p,
	'CE',
	WHEB_MENSAGEM_PCK.get_texto(312566), --Confirmação do edital da licitação.
	nr_seq_licitacao_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lic_confirmar_edital ( nr_seq_licitacao_p bigint, nm_usuario_p text) FROM PUBLIC;

