SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_consiste_gerar_proc_compra ( cd_estabelecimento_p bigint, nr_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_cot_compra_w			timestamp;
cd_conta_contabil_w		varchar(20);
ds_conta_contabil_w		varchar(255);
nr_seq_tipo_compra_w		bigint;
nr_seq_mod_compra_w		bigint;
ds_mod_compra_w			varchar(100);
vl_preco_liquido_w			double precision;
vl_disponivel_w			double precision;
ds_erro_w			varchar(255);

c01 CURSOR FOR 
SELECT	a.dt_cot_compra, 
	obter_conta_cont_item_cot(b.nr_cot_compra, b.nr_item_cot_compra) cd_conta, 
	substr(obter_desc_conta_contabil(obter_conta_cont_item_cot(b.nr_cot_compra, b.nr_item_cot_compra)),1,255) ds_conta, 
	b.nr_seq_tipo_compra, 
	b.nr_seq_mod_compra, 
	substr(lic_obter_desc_mod_compra(b.nr_seq_mod_compra),1,100) ds_mod_compra, 
	sum(c.vl_preco_liquido) vl_preco_liquido 
from	cot_compra a, 
	cot_compra_item b, 
	cot_compra_resumo_v c 
where	a.nr_cot_compra		= b.nr_cot_compra 
and	b.nr_cot_compra		= c.nr_cot_compra 
and	b.nr_item_cot_compra	= c.nr_item_cot_compra 
and	b.nr_cot_compra		= nr_cot_compra_p 
and	lic_obter_tipo_compra(b.nr_seq_tipo_compra) = 'D' 
group by 
	a.dt_cot_compra, 
	b.nr_seq_mod_compra, 
	b.nr_seq_tipo_compra, 
	b.nr_seq_tipo_compra, 
	obter_conta_cont_item_cot(b.nr_cot_compra, b.nr_item_cot_compra) 
order by 
	cd_conta, 
	nr_seq_mod_compra;
			

BEGIN 
 
open C01;
loop 
fetch C01 into 
	dt_cot_compra_w, 
	cd_conta_contabil_w, 
	ds_conta_contabil_w, 
	nr_seq_tipo_compra_w, 
	nr_seq_mod_compra_w, 
	ds_mod_compra_w, 
	vl_preco_liquido_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	vl_disponivel_w := 	(coalesce(lic_obter_vl_limite_conta(trunc(clock_timestamp(),'dd'), cd_conta_contabil_w, nr_seq_tipo_compra_w, nr_seq_mod_compra_w, cd_estabelecimento_p),0) - 
			coalesce(obter_valor_acum_oc_direta(cd_conta_contabil_w, cd_estabelecimento_p, nr_seq_tipo_compra_w, nr_seq_mod_compra_w),0) - 
			coalesce(obter_valor_pend_oc_direta(cd_conta_contabil_w, cd_estabelecimento_p, nr_seq_tipo_compra_w, nr_seq_mod_compra_w),0));
 
	 
	if (vl_disponivel_w < vl_preco_liquido_w) then 
		ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(303626,'DS_CONTA_CONTABIL_W='|| DS_CONTA_CONTABIL_W ||';DS_MOD_COMPRA_W='|| DS_MOD_COMPRA_W),1,255);
				/*O valor para compra direta superou o valor disponível. Verifique a Conta contábil= #@DS_CONTA_CONTABIL_W#@, e a Modalidade= #@DS_MOD_COMPRA_W#@.*/
 
 
	end if;	
	 
	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then	 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266176,'DS_ERRO=' || ds_erro_w);
	end if;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
