CREATE OR REPLACE PROCEDURE lic_estornar_aprov_aditivo ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_aditivo_w			double precision;
nr_seq_reg_compra_item_w		bigint;					
qt_saldo_w			double precision;

BEGIN
 
select	nr_seq_reg_compra_item, 
	qt_item, 
	lic_obter_saldo_item_solic(nr_seq_licitacao, nr_seq_lic_item) qt_saldo 
into STRICT	nr_seq_reg_compra_item_w, 
	qt_aditivo_w, 
	qt_saldo_w 
from	reg_lic_aditivo 
where	nr_sequencia = nr_sequencia_p;
 
 
if (qt_aditivo_w > qt_saldo_w) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266186);
	--'A quantidade do aditivo já foi utilizada, portanto, não pode ser estornado a aprovação.'); 
end if;
 
update	reg_compra_item 
set	qt_item		= qt_item - qt_aditivo_w 
where	nr_sequencia	= nr_seq_reg_compra_item_w;
 
update	reg_lic_aditivo 
set	dt_aprovacao	 = NULL, 
	nm_usuario_aprov	= '' 
where	nr_sequencia	= nr_sequencia_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
