SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_estornar_liberacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	reg_lic_edital
where	nr_seq_licitacao = nr_sequencia_p
and	(dt_homologacao_edital IS NOT NULL AND dt_homologacao_edital::text <> '');

if (qt_existe_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266188);
	--'Não é possível estornar a liberação, pois já foi homologado o edital desta licitação.');
end if;


update	reg_licitacao
set	dt_liberacao = '',
	nm_usuario_lib = ''
where	nr_sequencia = nr_sequencia_p;

insert into reg_lic_historico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	ie_tipo_historico,
	ds_observacao,
	nr_seq_licitacao)
values (	nextval('reg_lic_historico_seq'),
	clock_timestamp(),
	nm_usuario_p,
	'EL',
	WHEB_MENSAGEM_PCK.get_texto(312586), --Estorno da liberação.
	nr_sequencia_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
