SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_estornar_lib_reg_compra ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_licitacao_w				bigint;
nr_ordem_compra_w			bigint;
qt_existe_w				bigint;

c01 CURSOR FOR 
SELECT	nr_ordem_compra 
from	ordem_compra 
where	nr_seq_reg_lic_compra = nr_sequencia_p;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	count(*) 
	into STRICT	qt_existe_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_w 
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	 
	if (qt_existe_w > 0) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266189,'NR_ORDEM_COMPRA=' || nr_ordem_compra_w);
		--'A ordem de compra número ' || nr_ordem_compra_w || ' já está liberada. ' || 
		--'Portanto, não será mais possível desfazer esse registro de compras.'); 
	end if;
	 
	end;
end loop;
close C01;
 
update	reg_lic_compra 
set	dt_liberacao = '', 
	nm_usuario_lib = '' 
where	nr_sequencia = nr_sequencia_p;
 
select	nr_seq_licitacao 
into STRICT	nr_seq_licitacao_w 
from	reg_lic_compra 
where	nr_sequencia = nr_sequencia_p;
 
insert into reg_lic_historico( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	ie_tipo_historico, 
	ds_observacao, 
	nr_seq_licitacao) 
values (	nextval('reg_lic_historico_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	'ELR', 
	Wheb_mensagem_pck.get_Texto(303630, 'NR_SEQUENCIA_P='|| NR_SEQUENCIA_P), /*'Estorno da liberação do registro de compras número: ' || NR_SEQUENCIA_P,*/	 
	nr_seq_licitacao_w);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
