SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_estornar_processo_compras ( nr_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_solic_compra_w		bigint;

c01 CURSOR FOR
SELECT	distinct
	a.nr_solic_compra
from	cot_compra_item a
where	a.nr_cot_compra	= nr_cot_compra_p
and	(a.nr_solic_compra IS NOT NULL AND a.nr_solic_compra::text <> '')

union all

select	distinct
	b.nr_solic_compra
from	cot_compra_solic_agrup b,
	cot_compra_item a
where	a.nr_cot_compra	= nr_cot_compra_p
and	a.nr_cot_compra	= b.nr_cot_compra
and	a.nr_item_cot_compra = b.nr_item_cot_compra;


BEGIN

CALL lic_est_fechamento_compra_dir(nr_cot_compra_p,nm_usuario_p);
CALL lic_est_fechamento_compra_lic(nr_cot_compra_p,nm_usuario_p);

CALL gerar_historico_cotacao(
		nr_cot_compra_p,
		Wheb_mensagem_pck.get_Texto(303634), /*'Estono do fechamento da cotação de compras',*/
		Wheb_mensagem_pck.get_Texto(303635), /*'Foi estornado o fechamento desta cotação de compras, desfazendo todo o processo de geração da solicitação, cotação, ordem e licitação de compras.',*/
		'S',
		nm_usuario_p);

open C01;
loop
fetch C01 into
	nr_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	solic_compra
	set	dt_baixa	 = NULL,
		ds_observacao	= ''
	where	nr_solic_compra	= nr_solic_compra_w;

	update	solic_compra_item
	set	dt_baixa	 = NULL,
		cd_motivo_baixa	 = NULL
	where	nr_solic_compra	= nr_solic_compra_w;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lic_estornar_processo_compras ( nr_cot_compra_p bigint, nm_usuario_p text) FROM PUBLIC;

