SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_est_fechamento_compra_lic ( nr_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_solic_compra_w				bigint;
nr_cot_compra_w				bigint;
nr_sequencia_w				bigint;
qt_existe_w				bigint;
nr_cot_compra_origem_lic_w			bigint;

nr_cot_compra_ww			bigint;
nr_item_cot_compra_ww			bigint;
nr_seq_licitacao_ww			bigint;
nr_seq_lic_item_ww			bigint;

c01 CURSOR FOR
SELECT	a.nr_solic_compra,
	a.nr_cot_compra_origem_lic
from	solic_compra a
where	nr_cot_compra_origem_lic	= nr_cot_compra_p
and	(nr_cot_compra_origem_lic IS NOT NULL AND nr_cot_compra_origem_lic::text <> '')
and	lic_obter_tipo_forma_compra(a.nr_seq_forma_compra) = 'L'

union all

select	distinct
	a.nr_solic_compra,
	null
from	solic_compra a,
	solic_compra_item b
where	a.nr_solic_compra = b.nr_solic_compra
and	b.nr_cot_compra	= nr_cot_compra_p
and	coalesce(nr_cot_compra_origem_lic::text, '') = ''
and	lic_obter_tipo_forma_compra(a.nr_seq_forma_compra) = 'L';

c02 CURSOR FOR
SELECT	 distinct a.nr_cot_compra
from	 cot_compra a,
	 cot_compra_item b
where	 a.nr_cot_compra	= b.nr_cot_compra
and	 b.nr_solic_compra	= nr_solic_compra_w;

c03 CURSOR FOR
SELECT	distinct a.nr_sequencia
from	reg_licitacao a,
	cot_compra b
where	a.nr_sequencia	= b.nr_seq_reg_licitacao
and	b.nr_cot_compra	= nr_cot_compra_w;

c04 CURSOR FOR
SELECT	nr_cot_compra,
	nr_item_cot_compra,
	nr_seq_licitacao,
	nr_seq_lic_item
from	cot_compra_item
where	nr_cot_compra	= nr_cot_compra_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_solic_compra_w,
	nr_cot_compra_origem_lic_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_cot_compra_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		open C03;
		loop
		fetch C03 into
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin

			select	count(*)
			into STRICT	qt_existe_w
			from	reg_lic_edital
			where	nr_seq_licitacao = nr_sequencia_w
			and	(dt_homologacao_edital IS NOT NULL AND dt_homologacao_edital::text <> '');

			if (qt_existe_w > 0) then
				/*'O edital da licitação: ' || NR_SEQUENCIA_W || ' já foi homologado. ' ||
				'Portanto, não é possível estornar esse processo de compras.');*/
				CALL wheb_mensagem_pck.exibir_mensagem_abort(193472,'NR_SEQUENCIA_W='||NR_SEQUENCIA_W);
			end if;

			select	count(*)
			into STRICT	qt_existe_w
			from	reg_licitacao
			where	nr_sequencia = nr_sequencia_w
			and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

			if (qt_existe_w > 0) then
				/*'A licitação: ' || NR_SEQUENCIA_W || ' já está liberada. ' ||
				'Portanto, não é possível estornar esse processo de compras.');*/
				CALL wheb_mensagem_pck.exibir_mensagem_abort(193473,'NR_SEQUENCIA_W='||NR_SEQUENCIA_W);
			end if;

			open c04;
			loop
			fetch c04 into
				nr_cot_compra_ww,
				nr_item_cot_compra_ww,
				nr_seq_licitacao_ww,
				nr_seq_lic_item_ww;
			EXIT WHEN NOT FOUND; /* apply on c04 */
				begin

				update	cot_compra_item
				set	nr_seq_licitacao		 = NULL,
					nr_seq_lic_item		 = NULL
				where	nr_cot_compra		= nr_cot_compra_ww
				and	nr_item_cot_compra	= nr_item_cot_compra_ww;

				delete
				from	reg_lic_item
				where	nr_seq_licitacao	= nr_seq_licitacao_ww
				and	nr_seq_lic_item	= nr_seq_lic_item_ww;

				end;
			end loop;
			close c04;

			update	cot_compra
			set	nr_seq_reg_licitacao	 = NULL
			where	nr_cot_compra		= nr_cot_compra_w;

			select	count(*)
			into STRICT	qt_existe_w
			from	cot_compra_item
			where	nr_cot_compra <> nr_cot_compra_w
			and	nr_seq_licitacao = nr_sequencia_w;

			if (qt_existe_w = 0) then
				begin

				delete from reg_lic_edital
				where nr_seq_licitacao = nr_sequencia_w;

				delete from reg_lic_historico
				where nr_seq_licitacao = nr_sequencia_w;

				update	cot_compra
				set	nr_seq_reg_licitacao	 = NULL
				where	nr_seq_reg_licitacao	= nr_sequencia_w;

				delete from reg_licitacao
				where nr_sequencia = nr_sequencia_w;

				end;
			end if;

			end;
		end loop;
		close C03;

		if (nr_cot_compra_origem_lic_w > 0) then

			delete from cot_compra_forn
			where nr_cot_compra = nr_cot_compra_w;

			delete from cot_compra
			where nr_cot_compra = nr_cot_compra_w;
		end if;

		end;
	end loop;
	close C02;

	if (nr_cot_compra_origem_lic_w > 0) then
		delete from solic_compra
		where nr_solic_compra = nr_solic_compra_w;
	else
		update	solic_compra
		set	nr_seq_forma_compra = ''
		where	nr_solic_compra = nr_solic_compra_w;
	end if;

	end;
end loop;
close C01;

update	cot_compra
set	dt_fechamento_lic	= ''
where	nr_cot_compra	= nr_cot_compra_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lic_est_fechamento_compra_lic ( nr_cot_compra_p bigint, nm_usuario_p text) FROM PUBLIC;

