SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_gerar_hist_alt_item ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint, ds_campo_alterado_p text, ie_forma_alteracao_p text, vl_antigo_p bigint, vl_atual_p bigint, nm_usuario_p text) AS $body$
DECLARE

ds_texto_w		varchar(255);


BEGIN


if (ds_campo_alterado_p = 'QT_ITEM') then

	if (ie_forma_alteracao_p = 'M') then /*Manual*/
		ds_texto_w := WHEB_MENSAGEM_PCK.get_texto(310512) || nr_seq_lic_item_p || '.' || chr(13) || chr(10); --Alterado de forma manual a quantidade do item:
	elsif (ie_forma_alteracao_p = 'B') then /*Botão direito*/
		ds_texto_w := WHEB_MENSAGEM_PCK.get_texto(310543, 'NR_SEQ_LIC_ITEM_P=' || nr_seq_lic_item_p) || chr(13) || chr(10); --Alterado a quantidade do item: ' || nr_seq_lic_item_p || ' através da opção Alterar quantidade.'
	end if;

	insert into reg_lic_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		ie_tipo_historico,
		ds_observacao,
		nr_seq_licitacao)
	values (	nextval('reg_lic_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		'AQI',
		substr(ds_texto_w || WHEB_MENSAGEM_PCK.get_texto(310560) || campo_mascara_virgula_casas(vl_antigo_p,4) || '.' || chr(13) || chr(10) ||
					WHEB_MENSAGEM_PCK.get_texto(310573) || campo_mascara_virgula_casas(vl_atual_p,4) || '.',1,255),
		nr_seq_licitacao_p);
end if;

if (ds_campo_alterado_p = 'VL_MAXIMO_EDITAL') then

	if (ie_forma_alteracao_p = '0') then /*Vencedor da cotação*/
		ds_texto_w := WHEB_MENSAGEM_PCK.get_texto(310582, 'NR_SEQ_LIC_ITEM_P=' || nr_seq_lic_item_p) || chr(13) || chr(10); --Definido o preço do edital do item ' || nr_seq_lic_item_p || ' utilizando a opção Vencedor da cotação.
	elsif (ie_forma_alteracao_p = '1') then /*Última compra*/
		ds_texto_w := WHEB_MENSAGEM_PCK.get_texto(310598, 'NR_SEQ_LIC_ITEM_P=' || nr_seq_lic_item_p) || chr(13) || chr(10); --Definido o preço do edital do item ' || nr_seq_lic_item_p || ' utilizando a opção Última compra.
	elsif (ie_forma_alteracao_p = '2') then /*Última compra com inflação*/
		ds_texto_w := WHEB_MENSAGEM_PCK.get_texto(310601, 'NR_SEQ_LIC_ITEM_P=' || nr_seq_lic_item_p) || chr(13) || chr(10); --Definido o preço do edital do item ' || nr_seq_lic_item_p || ' utilizando a opção Última compra com inflação.
	elsif (ie_forma_alteracao_p = '3') then /*Média dos fornecedores consideráveis*/
		ds_texto_w := WHEB_MENSAGEM_PCK.get_texto(310602, 'NR_SEQ_LIC_ITEM_P=' || nr_seq_lic_item_p) || chr(13) || chr(10); --Definido o preço do edital do item ' || nr_seq_lic_item_p || ' utilizando a opção Média dos fornecedores consideráveis.
	elsif (ie_forma_alteracao_p = '99') then /*Manual*/
		ds_texto_w := WHEB_MENSAGEM_PCK.get_texto(310604) || nr_seq_lic_item_p || '.' || chr(13) || chr(10); --Alterado manualmente o preço do edital do item
	end if;

	insert into reg_lic_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		ie_tipo_historico,
		ds_observacao,
		nr_seq_licitacao)
	values (	nextval('reg_lic_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		'AVE',
		substr(ds_texto_w ||	WHEB_MENSAGEM_PCK.get_texto(310512)	|| campo_mascara_virgula_casas(vl_antigo_p,4) || '.' || chr(13) || chr(10) ||
					WHEB_MENSAGEM_PCK.get_texto(310573) || campo_mascara_virgula_casas(vl_atual_p,4) || '.',1,255),
		nr_seq_licitacao_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lic_gerar_hist_alt_item ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint, ds_campo_alterado_p text, ie_forma_alteracao_p text, vl_antigo_p bigint, vl_atual_p bigint, nm_usuario_p text) FROM PUBLIC;

