SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_gerar_item_venc (nr_seq_lic_p bigint) AS $body$
DECLARE


nr_seq_item_w		integer;
nr_seq_fornec_venc_w	integer;
nr_seq_item_venc_w	integer;
cont_w			integer;
nr_seq_venc_w		integer;
vl_item_w		double precision;

c01 CURSOR FOR
SELECT	nr_sequencia
from	lic_item
where	nr_seq_licitacao = nr_seq_lic_p;


BEGIN
open c01;
loop
fetch c01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	max(vl_item)
	into STRICT	vl_item_w
	from	lic_item_fornec
	where	nr_seq_item = nr_seq_item_w;

	if (vl_item_w = 0) then
		update	lic_item_fornec
		set	ie_vencedor = 'N'
		where	nr_seq_item = nr_seq_item_w;
	else
		select	max(nr_seq_fornec),
			count(nr_sequencia),
			max(nr_seq_item)
		into STRICT	nr_seq_fornec_venc_w,
			cont_w,
			nr_seq_item_venc_w
		from	lic_item_fornec
		where	nr_seq_item = nr_seq_item_w
		and	vl_item = (SELECT min(vl_item) from lic_item_fornec where nr_seq_item = nr_seq_item_w and vl_item <> 0);
		if (cont_w = 1) then   /*Se tiver apenas um com o menor preÃ§o*/
			CALL LIC_DEFINIR_ITEM_VENC(nr_seq_fornec_venc_w,nr_seq_item_venc_w);
		elsif (cont_w > 1) then
			update	lic_item_fornec
			set	ie_vencedor = 'N'
			where	nr_seq_item = nr_seq_item_w;
		end if;
	end if;
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
