CREATE OR REPLACE PROCEDURE lic_gerar_itens_homologacao ( nr_sequencia_p bigint, nr_seq_lic_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_licitacao_w			bigint;
nr_seq_lic_item_w			integer;
cd_material_w			integer;
nr_seq_parecer_w			bigint;
cd_cgc_fornec_w			varchar(14);
nr_seq_fornec_venc_w		bigint;



BEGIN

if (coalesce(nr_seq_lic_item_p,0) > 0) then
	begin

	select	a.nr_seq_licitacao
	into STRICT	nr_seq_licitacao_w
	from	reg_lic_homologacao a
	where	a.nr_sequencia = nr_sequencia_p;

	select	coalesce(max(cd_material),'')
	into STRICT	cd_material_w
	from	reg_lic_item
	where	nr_seq_licitacao	= nr_seq_licitacao_w
	and	nr_seq_lic_item		= nr_seq_lic_item_p;


	select	lic_obter_forn_vencedor_item(nr_seq_licitacao_w, nr_seq_lic_item_p)
	into STRICT	nr_seq_fornec_venc_w
	;

	insert into reg_lic_homol_itens(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_licitacao,
		nr_seq_homologacao,
		nr_seq_lic_item,
		cd_material,
		ie_homologado,
		nr_seq_fornec_venc)
	values (	nextval('reg_lic_homol_itens_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_licitacao_w,
		nr_sequencia_p,
		nr_seq_lic_item_p,
		cd_material_w,
		'P',
		nr_seq_fornec_venc_w);

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
