SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_gerar_ordem_compra ( nr_seq_licitacao_p bigint, nr_seq_item_fornec_p bigint, qt_material_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text, cd_local_estoque_p bigint) AS $body$
DECLARE

 
nr_seq_ordem_w			bigint;
cd_cgc_fornec_w			varchar(50);
cd_material_w			bigint;
qt_item_w			double precision;
cd_condicao_pagto_w		bigint;
cd_unid_medida_w			varchar(30);
vl_item_w				double precision;
nr_seq_item_w			bigint;
nr_seq_oci_w			bigint;
qt_dias_prazo_entrega_w		smallint;
cd_local_estoque_w		bigint;
cd_comprador_w			varchar(10);

 

BEGIN 
select	a.cd_material, 
	a.qt_item, 
	c.cd_cgc_fornec, 
	d.cd_condicao_pagto, 
	a.cd_unid_medida, 
	b.vl_item, 
	a.nr_sequencia, 
	d.qt_dias_prazo_entrega 
into STRICT	cd_material_w, 
	qt_item_w, 
	cd_cgc_fornec_w, 
	cd_condicao_pagto_w, 
	cd_unid_medida_w, 
	vl_item_w, 
	nr_seq_item_w, 
	qt_dias_prazo_entrega_w 
from	lic_item_fornec b, 
	lic_fornec c, 
	lic_item a, 
	lic_licitacao d 
where	a.nr_sequencia = b.nr_seq_item 
and	c.nr_sequencia = b.nr_seq_fornec 
and	d.nr_sequencia = a.nr_seq_licitacao 
and	b.nr_sequencia = nr_seq_item_fornec_p;
 
select	coalesce(max(cd_pessoa_fisica),0) 
into STRICT	cd_comprador_w 
from	comprador 
where	cd_estabelecimento = cd_estabelecimento_p 
and	ie_situacao = 'A';
 
if (cd_comprador_w = '0') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266193);
	--'Você não pode gerar a ordem de compra porque você não é um comprador. Foi verificado que não existe nenhum comprador cadastrado com o código da sua pessoa física.'); 
end if;
 
select	coalesce(max(a.nr_ordem_compra), 0) 
into STRICT	nr_seq_ordem_w 
from	ordem_compra a 
where	coalesce(a.dt_liberacao::text, '') = '' 
and	coalesce(a.dt_baixa::text, '') = '' 
and	coalesce(a.dt_aprovacao::text, '') = '' 
and	a.dt_ordem_compra between trunc(clock_timestamp(), 'dd') and fim_dia(clock_timestamp()) 
and	a.cd_cgc_fornecedor = cd_cgc_fornec_w 
and	a.ie_urgente		<> 'S' 
and	not exists						/*Edgar 05/03/2004, inclui este exists para não gerer para OC de cotação*/
 
		(SELECT	1 
		from		ordem_compra_item x 
		where		x.nr_ordem_compra	= a.nr_ordem_compra 
		and		(x.nr_cot_compra IS NOT NULL AND x.nr_cot_compra::text <> ''));
 
if (nr_seq_ordem_w = 0) then 
	select	nextval('ordem_compra_seq') 
	into STRICT	nr_seq_ordem_w 
	;
 
	insert into ordem_compra( 
		nr_ordem_compra, 
		cd_estabelecimento, 
		cd_cgc_fornecedor, 
		cd_condicao_pagamento, 
		cd_comprador, 
		dt_ordem_compra, 
		dt_atualizacao, 
		nm_usuario, 
		cd_moeda, 
		ie_situacao, 
		dt_inclusao, 
		cd_pessoa_solicitante, 
		ie_frete, 
		dt_entrega, 
		ie_aviso_chegada, 
		ie_emite_obs, 
		ie_urgente, 
		ie_somente_pagto, 
		ie_tipo_ordem, 
		nr_seq_lic) 
	values (	nr_seq_ordem_w, 
		cd_estabelecimento_p, 
		cd_cgc_fornec_w, 
		cd_condicao_pagto_w, 
		cd_comprador_w, 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		1, 
		'A', 
		clock_timestamp(), 
		cd_pessoa_fisica_p, 
		'C', 
		clock_timestamp() + qt_dias_prazo_entrega_w, 
		'N', 
		'S', 
		'N', 
		'N', 
		'L', 
		nr_seq_licitacao_p);
end if;
 
select	coalesce(max(nr_item_oci),0) + 1 
into STRICT	nr_seq_oci_w 
from	ordem_compra_item 
where	nr_ordem_compra = nr_seq_ordem_w;
 
if (coalesce(cd_local_estoque_p, 0) = 0) then 
	select	cd_local_estoque_padrao 
	into STRICT	cd_local_estoque_w 
	from	parametro_compras 
	where	cd_estabelecimento = cd_estabelecimento_p;
else 
	cd_local_estoque_w := cd_local_estoque_p;
end if;
 
insert into ordem_compra_item( 
	nr_ordem_compra, 
	nr_item_oci, 
	cd_material, 
	cd_unidade_medida_compra, 
	vl_unitario_material, 
	qt_material, 
	qt_original, 
	dt_atualizacao, 
	nm_usuario, 
	ie_situacao, 
	cd_pessoa_solicitante, 
	nr_seq_lic_item, 
	cd_local_estoque, 
	vl_total_item) 
values (	nr_seq_ordem_w, 
	nr_seq_oci_w, 
	cd_material_w, 
	cd_unid_medida_w, 
	vl_item_w, 
	qt_material_p, 
	qt_material_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	'A', 
	cd_pessoa_fisica_p, 
	nr_seq_item_w, 
	cd_local_estoque_w, 
	round((vl_item_w * qt_material_p)::numeric,4));
 
insert into ordem_compra_item_entrega( 
	nr_sequencia, 
	nr_ordem_compra, 
	nr_item_oci, 
	dt_prevista_entrega, 
	dt_entrega_original, 
	dt_entrega_limite, 
	qt_prevista_entrega, 
	dt_atualizacao, 
	nm_usuario) 
values (	nextval('ordem_compra_item_entrega_seq'), 
	nr_seq_ordem_w, 
	nr_seq_oci_w, 
	clock_timestamp() + qt_dias_prazo_entrega_w, 
	clock_timestamp() + qt_dias_prazo_entrega_w, 
	clock_timestamp() + qt_dias_prazo_entrega_w, 
	qt_material_p, 
	clock_timestamp(), 
	nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lic_gerar_ordem_compra ( nr_seq_licitacao_p bigint, nr_seq_item_fornec_p bigint, qt_material_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text, cd_local_estoque_p bigint) FROM PUBLIC;

