CREATE OR REPLACE PROCEDURE lic_gerar_reg_lic_fornec_docto ( nr_seq_fornec_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_licitacao_w			bigint;
nr_seq_mod_compra_w		bigint;
nr_seq_tipo_docto_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_seq_tipo_docto
from	reg_lic_mod_docto a
where	a.nr_seq_mod_compra	= nr_seq_mod_compra_w
and	a.ie_situacao		= 'A'
and not exists (
	SELECT	1
	from	reg_lic_fornec_docto x
	where	x.nr_seq_fornec		= nr_seq_fornec_p
	and	x.nr_seq_tipo_docto	= a.nr_seq_tipo_docto);


BEGIN

select	nr_seq_licitacao,
	lic_obter_modalidade_compra(nr_seq_licitacao) nr_seq_mod_compra
into STRICT	nr_seq_licitacao_w,
	nr_seq_mod_compra_w
from	reg_lic_fornec
where	nr_sequencia	= nr_seq_fornec_p;

open C01;
loop
fetch C01 into
	nr_seq_tipo_docto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into reg_lic_fornec_docto(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_fornec,
		nr_seq_tipo_docto,
		ie_revisado)
	values (	nextval('reg_lic_fornec_docto_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_fornec_p,
		nr_seq_tipo_docto_w,
		'N');
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
