CREATE OR REPLACE PROCEDURE lic_gerar_reg_lic_item_lance ( nr_seq_fornec_p bigint, nr_seq_lic_item_p bigint, vl_item_p bigint, ie_qualif_lance_fornec_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_licitacao_w			bigint;
nr_seq_lic_item_fornec_w		bigint;
vl_item_w				double precision;
ie_situacao_w			varchar(1);


BEGIN

select	coalesce(ie_situacao, 'A')
into STRICT	ie_situacao_w
from	reg_lic_fornec
where	nr_sequencia	= nr_seq_fornec_p;

if (ie_situacao_w = 'I') and (vl_item_p > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266199);
	--'Este fornecedor está Desabilitado, portanto, não pode participar deste lance. Favor informar valor 0 (zero).');
end if;

select	max(nr_seq_licitacao),
	max(nr_sequencia),
	coalesce(max(vl_item),0)
into STRICT	nr_seq_licitacao_w,
	nr_seq_lic_item_fornec_w,
	vl_item_w
from	reg_lic_item_fornec
where	nr_seq_fornec	= nr_seq_fornec_p
and	nr_seq_lic_item	= nr_seq_lic_item_p;

insert into reg_lic_item_lance(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_licitacao,
	nr_seq_fornec,
	nr_seq_lic_item_fornec,
	nr_seq_lic_item,
	dt_lance,
	vl_atual,
	vl_item,
	ie_situacao)
values (	nextval('reg_lic_item_lance_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_licitacao_w,
	nr_seq_fornec_p,
	nr_seq_lic_item_fornec_w,
	nr_seq_lic_item_p,
	clock_timestamp(),
	vl_item_w,
	vl_item_p,
	'A');

CALL atualiza_valor_reg_lic_item(nr_seq_lic_item_fornec_w, vl_item_p, ie_qualif_lance_fornec_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
