SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_grava_item_ordem_compra ( nr_ordem_compra_p bigint, cd_material_p bigint, cd_unidade_p text, vl_unitario_p bigint, qt_material_p bigint, ds_observacao_p text, nm_usuario_p text, dt_entrega_p timestamp, nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nr_seq_lic_item_p bigint, ds_marca_p text, nr_seq_licitacao_p bigint default 0) AS $body$
DECLARE


nr_item_w				integer 	:= 0;
ds_material_direto_w 			varchar(255);
cd_local_estoque_w			integer;
cd_centro_custo_w				integer;
cd_conta_contabil_w			varchar(20);
ie_aviso_chegada_w			varchar(01);
ie_geracao_w				varchar(01);
nr_seq_proj_rec_w				bigint;
ds_erro_w				varchar(255);
cd_estabelecimento_w			smallint;
ie_tipo_conta_w				integer	:= 2;
ie_material_estoque_w			varchar(1);
dt_ordem_compra_w			timestamp;
nr_seq_marca_w				bigint;
ie_tipo_ordem_w				ordem_compra.ie_tipo_ordem%type;
cd_cgc_fornecedor_w			ordem_compra.cd_cgc_fornecedor%type;
nr_seq_contrato_w			contrato.nr_sequencia%type;
nr_seq_regra_contrato_w			contrato_regra_nf.nr_sequencia%type;
ie_regra_local_w			varchar(15);

c01 CURSOR FOR
SELECT	a.nr_seq_contrato,
	c.nr_sequencia
from	reg_lic_contrato a,
	contrato b,
	contrato_regra_nf c
where	a.nr_seq_contrato = b.nr_sequencia
and	b.nr_sequencia = c.nr_seq_contrato
and	b.cd_cgc_contratado = cd_cgc_fornecedor_w
and	a.nr_seq_licitacao = nr_seq_licitacao_p
and	c.cd_material = cd_material_p;


BEGIN



select	coalesce(max(nr_item_oci),0) + 1
into STRICT	nr_item_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p;

begin
select	a.ds_material_direto,
	b.cd_centro_custo,
	b.cd_conta_contabil,
	b.cd_local_estoque,
	coalesce(b.ie_aviso_chegada,'N'),
	a.ie_geracao,
	nr_seq_proj_rec,
	cd_estabelecimento
into STRICT	ds_material_direto_w,
	cd_centro_custo_w,
	cd_conta_contabil_w,
	cd_local_estoque_w,
	ie_aviso_chegada_w,
	ie_geracao_w,
	nr_seq_proj_rec_w,
	cd_estabelecimento_w
from	solic_compra b,
	solic_compra_item a
where	a.nr_solic_compra			= b.nr_solic_compra
and	a.nr_solic_compra			= nr_solic_compra_p
and	a.nr_item_solic_compra		= nr_item_solic_compra_p;
exception
when others then
	ds_material_direto_w := ds_material_direto_w;
end;

select (max(obter_valor_param_usuario(917, 133, obter_perfil_ativo,nm_usuario_p, cd_estabelecimento_w)))
into STRICT	ie_regra_local_w
;

if (coalesce(cd_conta_contabil_w::text, '') = '') then
	if (coalesce(cd_centro_custo_w::text, '') = '') then
		ie_tipo_conta_w	:= 2;
	else
		ie_tipo_conta_w	:= 3;
	end if;

	select	dt_ordem_compra
	into STRICT	dt_ordem_compra_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_p;

	SELECT * FROM define_conta_material(	cd_estabelecimento_w, cd_material_p, ie_tipo_conta_w, null, null, null, null, null, null, null, cd_local_estoque_w, Null, dt_ordem_compra_w, cd_conta_contabil_w, cd_centro_custo_w, null) INTO STRICT cd_conta_contabil_w, cd_centro_custo_w;
end if;
	
select	coalesce(max(ie_material_estoque),'N')
into STRICT	ie_material_estoque_w
from	material_estab
where	cd_material = cd_material_p
and	cd_estabelecimento = obter_estab_ordem_compra(nr_ordem_compra_p);

if (ie_material_estoque_w = 'S') then
	begin
	select	ie_tipo_ordem
	into STRICT	ie_tipo_ordem_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_p;
	exception
	when others then
		ie_tipo_ordem_w := null;
	end;
	
	if	((ie_regra_local_w = 'S') or
		((ie_regra_local_w = 'A') and (coalesce(cd_local_estoque_w::text, '') = ''))) then

		select	coalesce(obter_regra_local_material(cd_estabelecimento_w,cd_material_p, 'O',ie_tipo_ordem_w), cd_local_estoque_w),
			coalesce(obter_regra_centro_custo_mat(cd_estabelecimento_w,cd_material_p, 'O',ie_tipo_ordem_w), cd_centro_custo_w)
		into STRICT	cd_local_estoque_w,
			cd_centro_custo_w
		;
	end if;
end if;

if (ds_marca_p IS NOT NULL AND ds_marca_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_marca_w
	from	marca
	where	elimina_acentuacao(upper(ds_marca)) = elimina_acentuacao(upper(ds_marca_p));
end if;

if (nr_seq_licitacao_p > 0) then
	
	select	max(cd_cgc_fornecedor)
	into STRICT	cd_cgc_fornecedor_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_p;
	
	open C01;
	loop
	fetch C01 into	
		nr_seq_contrato_w,
		nr_seq_regra_contrato_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_contrato_w		:= nr_seq_contrato_w;
		nr_seq_regra_contrato_w		:= nr_seq_regra_contrato_w;		
		exit;
		end;
	end loop;
	close C01;	
end if;

begin	
insert into ordem_compra_item(
	nr_ordem_compra,				nr_item_oci,
	cd_material,				cd_unidade_medida_compra,
	vl_unitario_material,				qt_material,			
	dt_atualizacao,				nm_usuario,
	ie_situacao,				vl_desconto,
	qt_material_entregue,			pr_descontos,
	cd_local_estoque,				ds_material_direto,
	ds_observacao,				cd_motivo_alteracao,
	cd_centro_custo,				nr_seq_proj_rec,
	cd_conta_contabil,				nr_solic_compra,
	nr_item_solic_compra,			ie_geracao_solic,
	nr_seq_reg_lic_item,			nr_seq_marca,
	qt_original,					vl_total_item,
	nr_contrato,				nr_seq_regra_contrato,
	cd_sequencia_parametro)
values (	nr_ordem_compra_p,			nr_item_w,		
	cd_material_p,				cd_unidade_p,
	vl_unitario_p,				qt_material_p,
	clock_timestamp(),					nm_usuario_p,
	'A',					0,
	0,					0,
	cd_local_estoque_w,			ds_material_direto_w,	
	ds_observacao_p,				null,
	cd_centro_custo_w,			nr_seq_proj_rec_w,
	cd_conta_contabil_w,			nr_solic_compra_p,
	nr_item_solic_compra_p,			ie_geracao_w,
	nr_seq_lic_item_p,			nr_seq_marca_w,
	qt_material_p,				round((vl_unitario_p * qt_material_p)::numeric,4),
	nr_seq_contrato_w,			nr_seq_regra_contrato_w,
	philips_contabil_pck.get_parametro_conta_contabil);
exception
when others then
	ds_erro_w	:= SQLERRM(SQLSTATE);
      	/*(-20011,'erro ao gravar ordem_compra_item: ' || chr(10) ||
	' Ordem: ' || nr_ordem_compra_p || 'Item: ' || nr_item_w || chr(10) ||
	' Solicitacao: ' || nr_solic_compra_p || ' Item: ' || nr_item_solic_compra_p || chr(10) ||
	' Material: ' || cd_material_p || chr(10) || ds_erro_w);*/
	CALL wheb_mensagem_pck.exibir_mensagem_abort(240484,'NR_ORDEM=' || nr_ordem_compra_p || ';NR_SOLIC=' || nr_solic_compra_p ||
							';CD_MATERIAL=' || cd_material_p || ';ERRO=' || ds_erro_w);
end;

insert into ordem_compra_item_entrega(
	nr_sequencia,				nr_ordem_compra,			
	nr_item_oci,				dt_prevista_entrega,
	qt_prevista_entrega,			dt_atualizacao,
	nm_usuario,				dt_entrega_original,
	dt_entrega_limite)
values (nextval('ordem_compra_item_entrega_seq'),	nr_ordem_compra_p,			
	nr_item_w,				dt_entrega_p,
	qt_material_p,				clock_timestamp(),
	nm_usuario_p,				dt_entrega_p,
	dt_entrega_p);

update	Solic_compra_item
set	dt_baixa			= clock_timestamp()
where	nr_solic_compra		= nr_solic_compra_p
and	nr_item_solic_compra	= nr_item_solic_compra_p;

CALL gerar_hist_solic_sem_commit(
		nr_solic_compra_p,
		wheb_mensagem_pck.get_texto(303740),
		wheb_mensagem_pck.get_texto(303741, 'NR_ITEM=' || nr_item_solic_compra_p)
		/*Item #@NR_ITEM#@ baixado pela procedure LIC_GRAVA_ITEM_ORDEM_COMPRA*/
,
		'B',
		'TASY');

if (ie_aviso_chegada_w = 'S') then
	update	ordem_compra
	set	ie_aviso_chegada		= 'S'
	where	nr_ordem_compra		= nr_ordem_compra_p
	and	ie_aviso_chegada		= 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lic_grava_item_ordem_compra ( nr_ordem_compra_p bigint, cd_material_p bigint, cd_unidade_p text, vl_unitario_p bigint, qt_material_p bigint, ds_observacao_p text, nm_usuario_p text, dt_entrega_p timestamp, nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nr_seq_lic_item_p bigint, ds_marca_p text, nr_seq_licitacao_p bigint default 0) FROM PUBLIC;

