SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_habilita_desabilita_fornec ( nr_seq_fornec_p bigint, ie_habilita_desabilita_p text, ds_motivo_desabilita_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_licitacao_w		bigint;
	

BEGIN 
 
if (nr_seq_fornec_p > 0) then 
	 
	select	nr_seq_licitacao 
	into STRICT	nr_seq_licitacao_w 
	from	reg_lic_fornec 
	where	nr_sequencia = nr_seq_fornec_p;
	 
end if;
 
if (nr_seq_fornec_p > 0) and (ie_habilita_desabilita_p = 'S') then 
	 
	update	reg_lic_fornec 
	set	ie_habilitado = 'S', 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_sequencia = nr_seq_fornec_p;
	 
	insert into reg_lic_historico( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		ie_tipo_historico, 
		ds_observacao, 
		nr_seq_licitacao) 
	values (	nextval('reg_lic_historico_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		'HAF', 
		substr(WHEB_MENSAGEM_PCK.get_texto(310619) || obter_fornec_reg_lic_fornec(nr_seq_fornec_p) || '.',1,255), --Habilitado o fornecedor 
		nr_seq_licitacao_w);
	 
elsif (ie_habilita_desabilita_p = 'N') then 
 
	update	reg_lic_fornec 
	set	ie_habilitado = 'N', 
		ie_situacao = 'I', 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp(), 
		ds_motivo_inativo = substr(ds_motivo_desabilita_p,1,255), 
		ds_motivo_desabilitado = substr(ds_motivo_desabilita_p,1,255) 
	where	nr_sequencia = nr_seq_fornec_p;
	 
	insert into reg_lic_historico( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		ie_tipo_historico, 
		ds_observacao, 
		nr_seq_licitacao) 
	values(	nextval('reg_lic_historico_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		'DEF', 
		substr(WHEB_MENSAGEM_PCK.get_texto(310620) || obter_fornec_reg_lic_fornec(nr_seq_fornec_p) || '.',1,255), --Desabilitado o fornecedor ' 
		nr_seq_licitacao_w);
		 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
