SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_homologar_edital ( nr_sequencia_p bigint, dt_homologacao_p timestamp, nr_controle_interno_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_licitacao_w				bigint;
nr_seq_modelo_w				varchar(15);
qt_existe_w				bigint;


BEGIN

select	nr_seq_licitacao
into STRICT	nr_seq_licitacao_w
from	reg_lic_edital
where	nr_sequencia	= nr_sequencia_p;

select	coalesce(lic_obter_modelo_modalidade(nr_seq_mod_compra),'X')
into STRICT	nr_seq_modelo_w
from	reg_licitacao
where	nr_sequencia	= nr_seq_licitacao_w;

if (nr_seq_modelo_w = 'C') then

	select	count(*)
	into STRICT	qt_existe_w
	from	reg_lic_documento_edital
	where	nr_seq_lic_edital	= nr_sequencia_p
	and	ie_tipo_documento	= 'F';

	if (qt_existe_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266200);
		--'A modelo da modalidade desta licitação é do tipo Contrato. Portanto, o edital deve possui uma documentação do tipo  Disponibilidade Financeira.');
	end if;

	select	count(*)
	into STRICT	qt_existe_w
	from	reg_lic_documento_edital
	where	nr_seq_lic_edital	= nr_sequencia_p
	and	ie_tipo_documento	= 'C';

	if (qt_existe_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266201);
		--'A modelo da modalidade desta licitação é do tipo Contrato. Portanto, o edital deve possui uma documentação do tipo  Disponibilidade Contábil.');
	end if;
end if;

update	reg_lic_edital
set	dt_homologacao_edital	= dt_homologacao_p,
	nr_controle_interno		= nr_controle_interno_p,
	nm_usuario_homolog	= nm_usuario_p
where	nr_sequencia 		= nr_sequencia_p;

insert into reg_lic_historico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	ie_tipo_historico,
	ds_observacao,
	nr_seq_licitacao)
values (	nextval('reg_lic_historico_seq'),
	clock_timestamp(),
	nm_usuario_p,
	'HE',
	WHEB_MENSAGEM_PCK.get_texto(310621) || nr_sequencia_p, --Lançamento do Edital número:
	nr_seq_licitacao_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
