SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_insere_doc_padrao ( nr_seq_licitacao_p bigint, nr_seq_doc_padrao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_objeto_w		varchar(4000);
dt_publicacao_w		varchar(15);
ds_material_w		varchar(255);
nr_lote_compra_w	varchar(80);
ds_lista_material_w	text;
ds_lista_lote_w		text;
vl_total_edital_w	double precision;
ds_titulo_w		varchar(255);
ds_texto_w		text;

c01 CURSOR FOR
SELECT	distinct
	substr(obter_desc_material(cd_material),1,255)
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p
and	coalesce(nr_lote_compra::text, '') = '';

c02 CURSOR FOR
SELECT	distinct
	nr_lote_compra
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p
and	(nr_lote_compra IS NOT NULL AND nr_lote_compra::text <> '');


BEGIN

select	max(to_char(dt_publicacao,'dd/mm/yyyy'))
into STRICT	dt_publicacao_w
from	reg_lic_public_edital
where	nr_seq_licitacao = nr_seq_licitacao_p;

select	ds_objeto
into STRICT	ds_objeto_w
from	reg_licitacao
where	nr_sequencia = nr_seq_licitacao_p;

select	sum(qt_item * coalesce(vl_maximo_edital,0))
into STRICT	vl_total_edital_w
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p
and	(cd_material IS NOT NULL AND cd_material::text <> '');

open C01;
loop
fetch C01 into
	ds_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_lista_material_w := ds_lista_material_w || ds_material_w || chr(13);
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_lote_compra_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	ds_lista_lote_w := ds_lista_lote_w || nr_lote_compra_w || chr(13);
	end;
end loop;
close C02;

select	ds_titulo,
	ds_texto
into STRICT	ds_titulo_w,
	ds_texto_w
from	lic_documentacao_padrao
where	nr_sequencia = nr_seq_doc_padrao_p;

insert into reg_lic_doc_padrao(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_licitacao,
	ds_titulo,
	ds_texto)
values (	nextval('reg_lic_doc_padrao_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_licitacao_p,
	ds_titulo_w,
	ds_texto_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lic_insere_doc_padrao ( nr_seq_licitacao_p bigint, nr_seq_doc_padrao_p bigint, nm_usuario_p text) FROM PUBLIC;

