SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lic_liberar_reg_compra ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;
nr_seq_licitacao_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	reg_lic_compra_item
where	nr_seq_reg_lic_compra = nr_sequencia_p;

if (qt_existe_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266232);
	--'Não é possível liberar um registro de compra sem nenhum item à gerar ordem de compra.');
end if;

update	reg_lic_compra
set	dt_liberacao = clock_timestamp(),
	nm_usuario_lib = nm_usuario_p
where	nr_sequencia = nr_sequencia_p;

select	nr_seq_licitacao
into STRICT	nr_seq_licitacao_w
from	reg_lic_compra
where	nr_sequencia = nr_sequencia_p;

insert into reg_lic_historico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	ie_tipo_historico,
	ds_observacao,
	nr_seq_licitacao)
values (	nextval('reg_lic_historico_seq'),
	clock_timestamp(),
	nm_usuario_p,
	'LR',
	WHEB_MENSAGEM_PCK.get_texto(310782) || nr_sequencia_p, --Liberação do registro de compras número:
	nr_seq_licitacao_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
