CREATE OR REPLACE PROCEDURE lic_retirar_item_vencedor ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_licitacao_w			bigint;
nr_seq_lic_item_w			bigint;
nr_seq_fornecedor_w		bigint;
ds_fornecedor_w			varchar(100);

BEGIN
 
update	reg_lic_item_fornec 
set	ie_vencedor = 'N', 
	ds_motivo_vencedor = '' 
where	nr_sequencia = nr_sequencia_p;
 
select	nr_seq_licitacao, 
	nr_seq_lic_item, 
	nr_seq_fornec 
into STRICT	nr_seq_licitacao_w, 
	nr_seq_lic_item_w, 
	nr_seq_fornecedor_w 
from	reg_lic_item_fornec 
where	nr_sequencia = nr_sequencia_p;
 
select	substr(max(obter_nome_pf_pj(null,cd_cgc_fornec)),1,100) 
into STRICT	ds_fornecedor_w 
from	reg_lic_fornec 
where	nr_sequencia = nr_seq_fornecedor_w;
 
insert into reg_lic_historico( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	ie_tipo_historico, 
	ds_observacao, 
	nr_seq_licitacao) 
values (	nextval('reg_lic_historico_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	'RV', 
	wheb_mensagem_pck.get_texto(795193,'NR_SEQ_LIC_ITEM_W='|| NR_SEQ_LIC_ITEM_W ||';DS_FORNECEDOR_W='|| DS_FORNECEDOR_W), /*Retirado (manualmente) o item #@NR_SEQ_LIC_ITEM_W#@ como vencedor para o fornecedor #@DS_FORNECEDOR_W#@.*/
 
	nr_seq_licitacao_w);
 
commit;
 
end;
$body$
