CREATE OR REPLACE PROCEDURE lic_troca_fornecedor ( nr_sequencia_p bigint, nr_seq_fornec_p bigint, nr_seq_lic_item_fornec_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_fornecedor_ant_w		varchar(100);
ds_fornecedor_novo_w		varchar(100);
nr_seq_licitacao_w			bigint;
nr_seq_lic_item_w			bigint;

BEGIN
 
select	substr(obter_fornec_reg_lic_fornec(nr_seq_fornec),1,100) ds_razao_social, 
	nr_seq_lic_item 
into STRICT	ds_fornecedor_ant_w, 
	nr_seq_lic_item_w 
from	reg_compra_item 
where	nr_sequencia = nr_sequencia_p;
 
select	substr(obter_nome_pf_pj(null,cd_cgc_fornec),1,100) ds_razao_social, 
	nr_seq_licitacao 
into STRICT	ds_fornecedor_novo_w, 
	nr_seq_licitacao_w 
from	reg_lic_fornec 
where	nr_sequencia = nr_seq_fornec_p;
 
update	reg_compra_item 
set	nr_seq_fornec = nr_seq_fornec_p, 
	nr_seq_lic_item_fornec = nr_seq_lic_item_fornec_p 
where	nr_sequencia = nr_sequencia_p;
 
insert into reg_lic_historico( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	ie_tipo_historico, 
	ds_observacao, 
	nr_seq_licitacao) 
values (	nextval('reg_lic_historico_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	'TF', 
	substr(wheb_mensagem_pck.get_texto(795203,'NR_SEQ_LIC_ITEM_W='|| NR_SEQ_LIC_ITEM_W ||';DS_FORNECEDOR_ANT_W='|| DS_FORNECEDOR_ANT_W ||';DS_FORNECEDOR_NOVO_W='|| DS_FORNECEDOR_NOVO_W),1,255), 
	nr_seq_licitacao_w);
 
commit;
 
end;
$body$
