CREATE OR REPLACE PROCEDURE lic_vincular_item_solic ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint, ie_altera_quantidade_p text, nm_usuario_p text) AS $body$
DECLARE

					 
qt_material_solic_w				double precision;					
qt_saldo_w				double precision;


BEGIN 
 
select	coalesce(qt_material,0) 
into STRICT	qt_material_solic_w 
from	solic_compra_item 
where	nr_solic_compra		= nr_solic_compra_p 
and	nr_item_solic_compra	= nr_item_solic_compra_p;
 
select	lic_obter_saldo_item_solic(nr_seq_licitacao_p,nr_seq_lic_item_p) 
into STRICT	qt_saldo_w
;
 
if (coalesce(ie_altera_quantidade_p,'N') = 'S') and (qt_saldo_w < qt_material_solic_w) then 
	update	solic_compra_item 
	set	qt_material = qt_saldo_w 
	where	nr_solic_compra		= nr_solic_compra_p 
	and	nr_item_solic_compra	= nr_item_solic_compra_p;
	 
	CALL gerar_historico_solic_compra( 
			nr_solic_compra_p,			 
			wheb_mensagem_pck.get_texto(303745) /*'Alteração na quantidade do item.'*/
, 
			/*Alterado a quantidade do item #@NR_ITEM#@ ao vincular o item com uma licitação que possuia saldo inferior a quantidade original da solicitação de compras. 
			Antes: #@QT_MATERIAL#@. 
			Depois: #@QT_SALDO#@.*/
 
			wheb_mensagem_pck.get_texto(303747, 
				'NR_ITEM=' || nr_item_solic_compra_p || 
				';QT_MATERIAL=' || qt_material_solic_w || 
				';QT_SALDO=' || qt_saldo_w), 
			'Q', 
			nm_usuario_p);
	 
end if;
 
insert into reg_lic_item_solic( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	nr_seq_licitacao, 
	nr_seq_lic_item, 
	nr_solic_compra, 
	nr_item_solic_compra, 
	qt_item) 
values (	nextval('reg_lic_item_solic_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	nr_seq_licitacao_p, 
	nr_seq_lic_item_p, 
	nr_solic_compra_p, 
	nr_item_solic_compra_p, 
	qt_saldo_w);
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
