CREATE OR REPLACE PROCEDURE limpar_componente_senha ( nm_usu_salvar_p text DEFAULT 'X', nm_maquina_p text DEFAULT 'X', cd_perfil_salvar_p bigint DEFAULT 0, cd_estab_salvar_p bigint DEFAULT 0) AS $body$
BEGIN
if (nm_usu_salvar_p <> 'X') then
	delete	FROM monitor_senha_custom
	where	nm_usuario = nm_usu_salvar_p;
elsif (nm_maquina_p <> 'X') then
	delete	FROM monitor_senha_custom
	where	nm_maquina = nm_maquina_p;
elsif (cd_perfil_salvar_p <> 0) then
	delete	FROM monitor_senha_custom
	where	cd_perfil = cd_perfil_salvar_p;
elsif (cd_estab_salvar_p <> 0) then
	delete	FROM monitor_senha_custom
	where	cd_estabelecimento = cd_estab_salvar_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
