SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE limpar_conpaci_log_estorno () AS $body$
DECLARE


qt_registros_w		bigint;
nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	conpaci_log_estorno
	where	dt_atualizacao < clock_timestamp() - interval '90 days' --Deixa 3 meses de registro de log apenas.
  LIMIT 10000;


BEGIN

qt_registros_w:= 0;

begin
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	qt_registros_w:= qt_registros_w + 1;

	begin
	delete	FROM conpaci_log_estorno
	where	nr_sequencia = nr_sequencia_w;
	exception
		when others then
		qt_registros_w:= qt_registros_w;
	end;

	if (qt_registros_w > 999) then
		commit;
		qt_registros_w:= 0;
	end if;

	end;
end loop;
close C01;
exception
	when others then
	qt_registros_w:= 0;
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE limpar_conpaci_log_estorno () FROM PUBLIC;

