SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE limpar_contagem_inventario ( nr_sequencia_p bigint, ie_todos_p text, nm_usuario_p text) AS $body$
DECLARE


ds_todos_w	varchar(255);
qt_existe_w	bigint;


BEGIN

update	inventario_material
set	qt_contagem		 = NULL,
	qt_recontagem		 = NULL,
	qt_seg_recontagem		 = NULL,
	qt_terc_recontagem		 = NULL,
	dt_contagem		 = NULL,
	dt_recontagem		 = NULL,
	dt_seg_recontagem		 = NULL,
	dt_terc_recontagem		 = NULL,
	qt_diferenca		 = NULL,
	qt_inventario		 = NULL,
	nm_usuario		= nm_usuario_p,
	nm_usuario_contagem	 = NULL,
	nm_usuario_recontagem	 = NULL,
	nm_usuario_seg_recontagem	 = NULL,
	nm_usuario_terc_recontagem	 = NULL,
	dt_atualizacao		= clock_timestamp(),
	ie_status_inventario		= 'P',
	ie_contagem_atual 		= '1'
where	nr_seq_inventario		= nr_sequencia_p
and	((ie_todos_p = 'S' and (qt_inventario IS NOT NULL AND qt_inventario::text <> '')) or (coalesce(qt_inventario::text, '') = '' and ie_todos_p = 'N'));
ds_todos_w		:= WHEB_MENSAGEM_PCK.get_texto(311030); --Todos os itens, ainda não definidos para atualização do saldo
if (ie_todos_p = 'S') then
	ds_todos_w	:= WHEB_MENSAGEM_PCK.get_texto(311050); --Todos os itens definidos para atualização do saldo
end if;

select	count(*)
into STRICT	qt_existe_w
from	inventario_material_lote
where	nr_seq_inventario = nr_sequencia_p;

if (qt_existe_w > 0) then
	begin
	update	 inventario_material_lote
	set	qt_contagem   = NULL,
		qt_recontagem  = NULL,
		qt_seg_recontagem  = NULL,
		qt_terc_recontagem  = NULL,
		qt_inventario  = NULL,
		qt_diferenca  = NULL,
		dt_atualizacao_nrec = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_seq_inventario = nr_sequencia_p
	and	((ie_todos_p = 'S' and (qt_inventario IS NOT NULL AND qt_inventario::text <> '')) or (coalesce(qt_inventario::text, '') = '' and ie_todos_p = 'N'));
	end;
end if;

insert into inventario_arq(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_inventario,
	ds_arquivo,
	ds_conteudo,
	ie_envio_retorno)
values (	nextval('inventario_arq_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_sequencia_p,
	WHEB_MENSAGEM_PCK.get_texto(311051), --Log de limpeza das contagens
	WHEB_MENSAGEM_PCK.get_texto(311052) || nm_usuario_p || chr(13) || chr(10) || ds_todos_w, --Efetuada a limpeza das contagens pelo usuário
	'R');

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
