CREATE OR REPLACE PROCEDURE limpar_dados_local_pa_mov ( cd_novo_setor_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_limpa_dados_saida_pa_w	varchar(1);
cd_estabelecimento_w		setor_atendimento.cd_estabelecimento_base%type;
cd_classif_setor_w			setor_atendimento.cd_classif_setor%type;
nr_seq_local_pa_w			atendimento_paciente.nr_seq_local_pa%type;


BEGIN

if (cd_novo_setor_p IS NOT NULL AND cd_novo_setor_p::text <> '' AND nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	max(cd_estabelecimento_base),
			max(cd_classif_setor)
	into STRICT	cd_estabelecimento_w,
			cd_classif_setor_w
	from	setor_atendimento
	where	cd_setor_atendimento	= cd_novo_setor_p;

	select	max(nr_seq_local_pa)
	into STRICT	nr_seq_local_pa_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;

	ie_limpa_dados_saida_pa_w  := obter_param_usuario(3111, 177, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_limpa_dados_saida_pa_w );

	if (coalesce(ie_limpa_dados_saida_pa_w,'N') = 'S') then
		if ( cd_classif_setor_w <> '1') and (nr_seq_local_pa_w IS NOT NULL AND nr_seq_local_pa_w::text <> '') then

		update	atendimento_paciente
		set	dt_chamada_paciente  = NULL,
			nr_seq_local_pa  = NULL,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_atendimento = nr_atendimento_p
		and	(nr_seq_local_pa IS NOT NULL AND nr_seq_local_pa::text <> '');

		end if;
	end if;

end if;

-- Commit
-- esta Rotina n√£o realiza o commit. Favor verificar onde a mesma foi implementada
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
