CREATE OR REPLACE PROCEDURE limpar_desc_mat ( nr_interno_conta_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_reg_w	 	bigint:= 0;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	material_atend_paciente
	where	nr_interno_conta = nr_interno_conta_p
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_reg_w
	from	mat_atend_paciente_valor
	where	nr_seq_material = nr_sequencia_w;

	if (qt_reg_w > 0) then
		delete	FROM mat_atend_paciente_valor
		where	nr_seq_material = nr_sequencia_w
		and	ie_tipo_valor = 3;
	end if;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
