CREATE OR REPLACE PROCEDURE limpar_fila_visu_senha (dias integer) AS $body$
BEGIN

    IF dias > 0 THEN
        DELETE FROM fila_visualizacao_senha
         WHERE coalesce(dt_visualizacao_senha::text, '') = ''
           AND dt_atualizacao < (clock_timestamp() - dias);
        COMMIT;
    END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
