SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE limpar_importacao_preco ( dt_competencia_p timestamp, ds_versao_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w	bigint;


BEGIN

if (dt_competencia_p IS NOT NULL AND dt_competencia_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	sus_preco_procaih
	where	dt_competencia = dt_competencia_p;

	while	qt_registro_w  > 0 LOOP

		Delete	FROM sus_preco_procaih
		where	dt_competencia = dt_competencia_p  LIMIT 1000;

/*		insert into log_exclusao(
			nm_tabela,
			dt_atualizacao,
			nm_usuario,
			ds_chave)
		values(	'SUS_PRECO_PROCAIH',
			sysdate,
			nm_usuario_p,
			'Competência: '||to_char(dt_competencia_p,'dd/mm/yyyy')||' ATESUS_F8 - F5');*/
		CALL gravar_log_exclusao('SUS_PRECO_PROCAIH',nm_usuario_p,'Competência: '||to_char(dt_competencia_p,'dd/mm/yyyy')||' ATESUS_F8 - F5','N');

		commit;

		qt_registro_w	:= qt_registro_w - 1000;

	end loop;
elsif (ds_versao_p IS NOT NULL AND ds_versao_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	sus_preco_procaih
	where	ie_versao = ds_versao_p;

	while	qt_registro_w  > 0 LOOP

		Delete	FROM sus_preco_procaih
		where	ie_versao = ds_versao_p  LIMIT 1000;

		/*insert into log_exclusao(
			nm_tabela,
			dt_atualizacao,
			nm_usuario,
			ds_chave)
		values(	'SUS_PRECO_PROCAIH',
			sysdate,
			nm_usuario_p,
			'Versão: '||ds_versao_p ||' ATESUS_F8 - F6');*/
		CALL gravar_log_exclusao('SUS_PRECO_PROCAIH',nm_usuario_p,'Versão: '||ds_versao_p ||' ATESUS_F8 - F6','N');

		commit;

		qt_registro_w	:= qt_registro_w - 1000;
	end loop;
end if;


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
